"use strict";var oDiv=document.querySelector(".classListShop");oDiv.addEventListener("click",function(e){if("all"===e.target.getAttribute("name")&&cateArr.forEach(function(t){t.buy=!0}),"on"===e.target.getAttribute("name")&&cateArr.forEach(function(t){t.buy=!1}),"not"===e.target.getAttribute("name")&&cateArr.forEach(function(t){t.buy=!t.buy}),"checked"===e.target.getAttribute("name")){var r=e.target.getAttribute("goods_id");cateArr.forEach(function(t){t.goods_id===r&&(t.buy=$(e.target).prop("checked"))})}if("del"===e.target.getAttribute("name")){var a=e.target.getAttribute("goods_id");cateArr.forEach(function(t,e){t.goods_id===a&&cateArr.splice(e,1)})}if("odd"===e.target.getAttribute("name")){var o=e.target.getAttribute("goods_id");cateArr.forEach(function(t,e){t.goods_id===o&&t.num++})}if("lost"===e.target.getAttribute("name")){var i=e.target.getAttribute("goods_id");cateArr.forEach(function(t,e){t.goods_id===i&&t.num--})}setPage(cateArr),localStorage.setItem("cart",JSON.stringify(cateArr))});